import { Component } from '@angular/core';

@Component({
  selector: 'app-pocket-animation',
  templateUrl: './pocket-animation.component.html',
  styleUrls: ['./pocket-animation.component.css']
})
export class PocketAnimationComponent {
  showNoMoney = false;

  constructor() { }

  ngOnInit(): void {
    // Después de 4 segundos, mostrar el mensaje "¡Sin dinero!"
    setTimeout(() => {
      this.showNoMoney = true;
    }, 4000);
  }
}
